import { useState, useRef } from 'react';
import './App.css';

function App() {
  const [products, setProducts] = useState([])
  const [cakes, setCakes] = useState([])
  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [used, setUsed] = useState(0)
  const [total, setTotal] = useState(0)
  const id = useRef(0)

  // prodcut: {id, name, price, portion, total}
  const addProduct = (p) => {
    setProducts([...products, p])
  }
  const removeProductByName = (name) => {
    let temp = products
    let index = -1
    for (var i = 0; i < products.length; i++){
      if (products[i].name == name){
        index = i
        break
      }
    }
    if (index != -1){
      temp.splice(index, 1)
      setProducts(temp)
    } else throw new Error('There is no product with such name in the products list!')
  }
  const nameChange = e => setName(e.target.value)
  const namePrice = e => setPrice(e.target.value)
  const nameUsed = e => setUsed(e.target.value)
  const nameTotal = e => setTotal(e.target.value)
  const handleAdd = () => {
    const newProduct = {id: id.current, name: name, price: price, used: used, total: total}
    addProduct(newProduct)
    id.current = id.current + 1
  }
  console.log(products)
  return (
    <div className="App">
      <div className='Form'>
        <div className='Form__tag'>Name</div>
        <input className='Form__inputName' value={name} onChange = {nameChange}></input>
        <div className='Form__tag'>Price</div>
        <input className='Form__inputPrice'  value={price} onChange = {namePrice}></input>
        <div className='Form__tag'>Used</div>
        <input className='Form__inputUsed'  value={used} onChange = {nameUsed}></input>
        <div className='Form__tag'>Total</div>
        <input className='Form__inputTotal'  value={total} onChange = {nameTotal}></input>
        <button onClick={handleAdd} >Add</button>
      </div>

      <div className='Products'>
        {products.map(product => {
          return <Product product = {product}/>
        })}
      </div>
    </div>
  );
}
function Product({product}){
  const {name, price, used, total} = product
  return(
    <div className="Product">
      <div className="Product__name">{name}</div>
      <div className="Product__price">{price}</div>
      <div className="Product__used">{used}</div>
      <div className="Product__total">{total}</div>
    </div>
  ); 
}

export default App;
